        method: "PUT",
        body: JSON.stringify(updates),
      });
      return json<Connector>(res);
    },

    deleteConnector: async (id: string): Promise<void> => {
      await apiFetch(`/api/connectors/${encodeURIComponent(id)}`, { method: "DELETE" });
