import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { useApp } from "@/context/AppContext";
import { useLocation } from "wouter";
import { Eye, EyeOff } from "lucide-react";
import { z } from "zod";

// Login Schema
const loginSchema = z.object({
  username: z.string().min(1, "Username is required"),
  password: z.string().min(1, "Password is required"),
});

type LoginCredentials = z.infer<typeof loginSchema>;

export default function Login() {
  const { t, checkAuth } = useApp();
  const { toast } = useToast();
  const [, navigate] = useLocation();
  const [showPassword, setShowPassword] = useState(false);

  const form = useForm<LoginCredentials>({
    resolver: zodResolver(loginSchema),
    defaultValues: { username: "", password: "" },
    mode: "onSubmit",
  });

  // Keycloak Login Mutation
  const loginMutation = useMutation({
    mutationFn: async (values: LoginCredentials) => {
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(values),
      });

      const data = await response.json();

      if (!data.success) {
        throw new Error(data.message || "Login failed");
      }

      return data;
    },
    onSuccess: async (data) => {
      toast({ 
        title: "Success", 
        description: data.message || "Logged in successfully" 
      });
      await checkAuth(); // Update user state
      navigate("/");
    },
    onError: (err: any) => {
      toast({
        title: "Error",
        description: err.message || "Invalid credentials",
        variant: "destructive",
      });
    },
  });

  // Demo Login Mutation
  const demoLoginMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ isDemo: true }),
      });

      const data = await response.json();

      if (!data.success) {
        throw new Error(data.message || "Demo login failed");
      }

      return data;
    },
    onSuccess: async (data) => {
      toast({ 
        title: "Demo Mode", 
        description: "Demo login successful" 
      });
      await checkAuth(); // Update user state
      navigate("/");
    },
    onError: (err: any) => {
      toast({
        title: "Error",
        description: err.message || "Demo login failed",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (values: LoginCredentials) => {
    loginMutation.mutate(values);
  };

  const handleDemoLogin = () => {
    demoLoginMutation.mutate();
  };

  return (
    <div className="fixed inset-0 flex flex-col">
      {/* Background */}
      <img
        src="/background.svg"
        alt="Background"
        aria-hidden="true"
        className="absolute inset-0 w-full h-full object-cover object-top pointer-events-none select-none"
      />
      {/* Overlay */}
      <div className="absolute inset-0 bg-black/30" aria-hidden="true" />

      {/* Content */}
      <div className="relative z-10 flex-1 flex items-center justify-center p-4">
        <Card className="w-full max-w-lg shadow-2xl bg-white/95 backdrop-blur-sm border-0">
          <CardContent className="p-8">
            {/* Logo + Title */}
            <div className="text-center mb-8">
              <div className="flex justify-center mb-6">
                <img
                  src="/ARENA2036_logomark_orange.svg"
                  alt="ARENA2036 Logo"
                  className="w-20 h-20"
                  onError={(e) => {
                    (e.currentTarget as HTMLImageElement).style.display = "none";
                  }}
                />
              </div>
              <h1 className="text-2xl font-bold text-gray-900" data-testid="login-title">
                {t("arena2036")}
              </h1>
              <p className="text-gray-600 mt-2" data-testid="login-subtitle">
                {t("edcManagementConsole")}
              </p>
              <p className="text-sm text-gray-500 mt-2">
                Client ID: CX-EDC
              </p>
            </div>

            {/* Login Form */}
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              {/* Username */}
              <div className="space-y-2">
                <Label htmlFor="username" className="text-sm font-medium text-gray-700">
                  {t("username")}
                </Label>
                <Input
                  {...form.register("username")}
                  id="username"
                  type="text"
                  placeholder="Enter username"
                  className="w-full"
                  data-testid="login-username"
                />
                {form.formState.errors.username && (
                  <p className="text-sm text-red-600" data-testid="login-username-error">
                    {form.formState.errors.username.message}
                  </p>
                )}
              </div>

              {/* Password */}
              <div className="space-y-2">
                <Label htmlFor="password" className="text-sm font-medium text-gray-700">
                  {t("password")}
                </Label>
                <div className="relative">
                  <Input
                    {...form.register("password")}
                    id="password"
                    type={showPassword ? "text" : "password"}
                    placeholder="Enter password"
                    className="w-full pr-10"
                    data-testid="login-password"
                  />
                  <button
                    type="button"
                    className="absolute inset-y-0 right-0 flex items-center pr-3"
                    onClick={() => setShowPassword(!showPassword)}
                    data-testid="toggle-password"
                  >
                    {showPassword ? (
                      <EyeOff className="h-4 w-4 text-gray-400 hover:text-gray-600" />
                    ) : (
                      <Eye className="h-4 w-4 text-gray-400 hover:text-gray-600" />
                    )}
                  </button>
                </div>
                {form.formState.errors.password && (
                  <p className="text-sm text-red-600" data-testid="login-password-error">
                    {form.formState.errors.password.message}
                  </p>
                )}
              </div>

              {/* Submit Button */}
              <Button
                type="submit"
                disabled={loginMutation.isPending}
                className="w-full bg-[#F28C00] hover:bg-[#d67b00] text-white"
                data-testid="login-submit"
              >
                {loginMutation.isPending ? (
                  <div className="flex items-center justify-center">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                    Logging in...
                  </div>
                ) : (
                  "Login with Keycloak"
                )}
              </Button>

              {/* Demo Login Button */}
              <Button
                type="button"
                onClick={handleDemoLogin}
                disabled={demoLoginMutation.isPending}
                variant="outline"
                className="w-full border-gray-300 hover:bg-gray-50"
                data-testid="demo-login"
              >
                {demoLoginMutation.isPending ? (
                  <div className="flex items-center justify-center">
                    <div className="w-4 h-4 border-2 border-gray-400 border-t-transparent rounded-full animate-spin mr-2" />
                    Demo Login...
                  </div>
                ) : (
                  "Demo Login (Testing)"
                )}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>

      {/* Copyright Footer */}
      <div className="relative z-10 text-center py-4">
        <p className="text-white text-sm">
          Copyright © Catena-X Automotive Network
        </p>
      </div>
    </div>
  );
}

