client/src/components/dashboard/Connecto.content:
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { useApp } from "@/context/AppContext";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { Edit, Trash2, Plus } from "lucide-react";
import { useState } from "react";
import { IntroWizardModal } from "@/components/modals/IntroWizardModal";
import { AddEditConnectorModal } from "@/components/modals/AddEditConnectorModal";
import { ConfirmDeleteDialog } from "@/components/modals/ConfirmDeleteDialog";
import { Connector } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";

export function ConnectorsTable() {
  const { t } = useApp();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [showIntroWizard, setShowIntroWizard] = useState(false);
  const [showAddEditModal, setShowAddEditModal] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [selectedConnector, setSelectedConnector] = useState<Connector | null>(null);
  const [connectorToDelete, setConnectorToDelete] = useState<Connector | null>(null);
  const [wizardData, setWizardData] = useState<any>(null);

  const { data: connectors = [], isLoading } = useQuery({
    queryKey: ["/api/connectors"],
    queryFn: api.getConnectors,
  });

  const deleteMutation = useMutation({
    mutationFn: api.deleteConnector,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/connectors"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stats"] });
      toast({
        title: "Success",
        description: "Connector deleted successfully",
      });
      setShowDeleteDialog(false);
      setConnectorToDelete(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete connector",
        variant: "destructive",
      });
    },
  });

  const handleAddConnector = () => {
    setShowIntroWizard(true);
  };

  const handleContinueFromWizard = (deploymentData?: any) => {
    setShowIntroWizard(false);
    setSelectedConnector(null);
    setWizardData(deploymentData);
    setShowAddEditModal(true);
  };

  const handleEditConnector = (connector: Connector) => {
    setSelectedConnector(connector);
    setShowAddEditModal(true);
  };

  const handleDeleteConnector = (connector: Connector) => {
    setConnectorToDelete(connector);
    setShowDeleteDialog(true);
  };

  const confirmDelete = () => {
    if (connectorToDelete) {
      deleteMutation.mutate(connectorToDelete.id);
    }
  };

  return (
    <>
      <Card className="bg-white shadow-sm border border-gray-200">
        <CardHeader className="px-6 py-4 border-b border-gray-200 flex flex-row justify-between items-center space-y-0">
          <h2 className="text-lg font-semibold text-gray-800" data-testid="connectors-table-title">
            {t("manageConnectors")}
          </h2>
          <Button
            onClick={handleAddConnector}
            className="bg-[var(--arena-orange)] hover:bg-[var(--arena-orange-hover)] text-white"
            data-testid="add-connector-button"
          >
            <Plus className="h-4 w-4 mr-2" />
            {t("addEdc")}
          </Button>
        </CardHeader>

        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow className="bg-gray-50">
                  <TableHead className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    {t("name")}
                  </TableHead>
                  <TableHead className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    {t("version")}
                  </TableHead>
                  <TableHead className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    BPN
                  </TableHead>
                  <TableHead className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    {t("status")}
                  </TableHead>
                  <TableHead className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    {t("endpoint")}
                  </TableHead>
                  <TableHead className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    {t("actions")}
                  </TableHead>
                </TableRow>
              </TableHeader>
              <TableBody className="bg-white divide-y divide-gray-200">
                {isLoading ? (
                  <TableRow>
                    <TableCell colSpan={6} className="px-6 py-4 text-center text-gray-500">
                      Loading connectors...
                    </TableCell>
                  </TableRow>
                ) : connectors.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} className="px-6 py-4 text-center text-gray-500">
                      No connectors found. Click "Add EDC" to create your first connector.
                    </TableCell>
                  </TableRow>
                ) : (
                  connectors.map((connector) => (
                    <TableRow key={connector.id} data-testid={`connector-row-${connector.id}`}>
                      <TableCell className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        {connector.name}
                      </TableCell>
                      <TableCell className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {connector.version}
                      </TableCell>
                      <TableCell className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {connector.bpn}
                      </TableCell>
                      <TableCell className="px-6 py-4 whitespace-nowrap">
                        <Badge 
                          variant={connector.status === "Connected" ? "default" : "secondary"}
                          className={connector.status === "Connected" ? "bg-green-100 text-green-800" : ""}
                        >
                          {connector.status}
                        </Badge>
                      </TableCell>
                      <TableCell className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {connector.endpoint}
                      </TableCell>
                      <TableCell className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleEditConnector(connector)}
                          className="text-[var(--arena-orange)] hover:text-[var(--arena-orange-hover)]"
                          data-testid={`edit-connector-${connector.id}`}
                        >
                          <Edit className="h-4 w-4 mr-1" />
                          {t("edit")}
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleDeleteConnector(connector)}
                          className="text-red-600 hover:text-red-800"
                          data-testid={`delete-connector-${connector.id}`}
                        >
                          <Trash2 className="h-4 w-4 mr-1" />
                          {t("delete")}
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      <IntroWizardModal
        open={showIntroWizard}
        onClose={() => setShowIntroWizard(false)}
        onContinue={handleContinueFromWizard}
      />

      <AddEditConnectorModal
        open={showAddEditModal}
        onClose={() => {
          setShowAddEditModal(false);
          setSelectedConnector(null);
          setWizardData(null);
        }}
        connector={selectedConnector}
        wizardData={wizardData}
      />

      <ConfirmDeleteDialog
        open={showDeleteDialog}
        onClose={() => {
          setShowDeleteDialog(false);
          setConnectorToDelete(null);
        }}
        onConfirm={confirmDelete}
        isLoading={deleteMutation.isPending}
      />
    </>
  );
}

client/src/components/dashboard/KpiCards.content:
import { Card, CardContent } from "@/components/ui/card";
import { useApp } from "@/context/AppContext";
import { useQuery } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { Skeleton } from "@/components/ui/skeleton";

export function KpiCards() {
  const { t } = useApp();

  const { data: stats, isLoading, error } = useQuery({
    queryKey: ["/api/sde/stats"],
    queryFn: api.getSdeStats, // 👈 lädt SDE + fallback
  });

  const kpiItems = [
    { key: "numberOfConnectors", value: stats?.connectors },
    { key: "assets", value: stats?.assets },
    { key: "policies", value: stats?.policies },
    { key: "contracts", value: stats?.contracts },
    { key: "contractAgreements", value: stats?.contractAgreements },
    { key: "dataOffers", value: stats?.dataOffers },
  ];

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {Array.from({ length: 6 }).map((_, i) => (
          <Card key={i} className="border border-gray-200">
            <CardContent className="p-6">
              <Skeleton className="h-4 w-32 mb-2" />
              <Skeleton className="h-8 w-16" />
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-sm text-red-600">
        Failed to load KPIs from SDE
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {kpiItems.map((item) => (
        <Card key={item.key} className="bg-white shadow-sm border border-gray-200">
          <CardContent className="p-6">
            <div className="flex justify-between items-start">
              <h3 className="text-sm font-medium text-gray-600" data-testid={`kpi-${item.key}-label`}>
                {t(item.key as any)}
              </h3>
            </div>
            <p className="text-2xl font-bold text-gray-900 mt-2" data-testid={`kpi-${item.key}-value`}>
              {item.value ?? 0}
            </p>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}

